"use strict";

var _login = _interopRequireDefault(require("../pageobjects/login.page"));
function _interopRequireDefault(obj) { return obj && obj.__esModule ? obj : { default: obj }; }
// import SecurePage from '../pageobjects/secure.page';

describe('My Login application', () => {
  beforeAll('Navigate to url', () => {
    browser.url("https://www.saucedemo.com/");
  });
  it('Should not login with empty inputs', async () => {
    await _login.default.inputUsername.setValue("");
    await _login.default.inputPassword.setValue("");
    await _login.default.btnLogin.click();
    // await browser.pause(1500); CASO ESPECFICO, NO BUENA PRCTICA
    // await loginPage.errorMsg.waitForDisplayed({timeout: 10000});
    await expect(_login.default.errorMsg).toBeDisplayed();
    await expect(_login.default.errorMsg).toHaveText("Epic sadface: Username is required");
  });
  it('Should not login with empty password', async () => {
    await _login.default.inputUsername.setValue("sgddfj");
    await _login.default.inputPassword.setValue("");
    await _login.default.btnLogin.click();
    await expect(_login.default.errorMsg).toBeDisplayed();
    await expect(_login.default.errorMsg).toHaveText("Epic sadface: Password is required");
    await browser.refresh();
  });
  it('Should not login with empty Username', async () => {
    await _login.default.inputUsername.setValue("");
    await _login.default.inputPassword.setValue("secret_sauce");
    await _login.default.btnLogin.click();
    await expect(_login.default.errorMsg).toBeDisplayed();
    await expect(_login.default.errorMsg).toHaveText("Epic sadface: Username is required");
  });
  it('Should not login with invalid creentials', async () => {
    await _login.default.login('standard_user', 'test');
    await expect(_login.default.errorMsg).toBeDisplayed();
    await expect(_login.default.errorMsg).toHaveText("Epic sadface: Username and password do not match any user in this service");
  });
  it('Should login with valid credentials', async () => {
    await _login.default.login('standard_user', 'secret_sauce');
    await expect(browser).toHaveUrl('https://www.saucedemo.com/inventory.html');
    await _login.default.btnBurgerMenu.click();
    await _login.default.btnLogout.click();
    await browser.refresh();
    // await expect(loginPage.errorMsg).toHaveText()
  });
  //USER 2
